const portfolioDropdownBtn=document.getElementById("portfolio-link");const tabLinks=document.getElementsByClassName("main-card-nav-link");portfolioDropdownBtn.addEventListener("click",()=>{portfolioDropdownBtn.classList.toggle("dripped");portfolioDropdownBtn.classList.toggle("dropped")});for(const link of tabLinks){link.addEventListener("click",()=>{const tabName=link.getAttribute("tab");document.querySelector(".active-tab").classList.remove("active-tab");document.querySelector(`#${tabName}-tab`).classList.add("active-tab");if(!link.classList.contains("port")){document.querySelector(".active-link").classList.remove("active-link");document.querySelector(`#${tabName}-link`).classList.add("active-link")}
if(link.classList.contains("port")){portfolioDropdownBtn.classList.add("dropped")}})}